	//NA R
			public static int[] MultiplySameArray() {
				
				int[] arr = {1,2,3,4,5};
				int[] arrMul = new int[arr.length];
				
				for(int i=0;i<arr.length;i++) {
					arrMul[i]=arr[i]*arr[i];
				}
				
				return arrMul;
				
			}
			
			
			public static void main(String[] args) {
		
				int[] arrMul= MultiplySameArray();
				
				System.out.println("Multiplication of same array:");
				
				for(int i=0;i<arrMul.length;i++) {
					System.out.print(arrMul[i]+" ");
				}

			}
	


